select singer.`name` singerName,song.`name` name,song.introduction,song.pic,song.url,song.lyric,song.id,song.singer_id
from singer,song
where song.singer_id = singer.id and song.id = #{id, jdbcType=INTEGER}

select singer.`name` singerName,song.`name` name,song.introduction,song.pic,song.url,song.lyric,song.id,song.singer_id
 from song,singer
 where  singer.id = song.singer_id and  song.name like #{name, jdbcType=VARCHAR}

select singer.`name` singerName,song.`name` name,song.introduction,song.pic,song.url,song.lyric,song.id,song.singer_id
from singer,song
where singer.id = song.singer_id and  singer.name like #{name, jdbcType=VARCHAR}
